import React, { useState, useEffect, useRef } from 'react';

const Card = ({ children, className = '' }) => (
  <div className={`bg-white rounded-lg shadow p-4 ${className}`}>
    {children}
  </div>
);

const isCoordinates = (input) => {
  const coordPattern = /^[-+]?([1-8]?\d(\.\d+)?|90(\.0+)?),\s*[-+]?(180(\.0+)?|((1[0-7]\d)|([1-9]?\d))(\.\d+)?)$/;
  return coordPattern.test(input);
};


const Input = ({ ...props }) => (
  <input
    {...props}
    className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
  />
);

const Button = ({ onClick, children, disabled }) => (
  <button
    onClick={onClick}
    className={`px-4 py-2 rounded-lg text-white font-medium ${
      disabled 
        ? 'bg-gray-400 cursor-not-allowed' 
        : 'bg-blue-500 hover:bg-blue-600 active:bg-blue-700'
    }`}
    disabled={disabled}
  >
    {children}
  </button>
);

const PropertyAssessmentTool = () => {
  const [searchInput, setSearchInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState('');
  const [googleLoaded, setGoogleLoaded] = useState(false);
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);
  const [searchCircle, setSearchCircle] = useState(null);
  const mapRef = useRef(null);

  const GOOGLE_API_KEY = 'AIzaSyBf5WWjQksCtxIziKvs7kFq4qjPCf6SoZQ';
  const SEARCH_RADIUS = 3000; // 3km radius

  useEffect(() => {
    // Check if Google Maps is already loaded
    if (window.google && window.google.maps) {
      setGoogleLoaded(true);
      initMap();
      return;
    }

    // Create a callback function that Google Maps can call when loaded
    window.initGoogleMaps = () => {
      setGoogleLoaded(true);
      initMap();
    };

    // Load the Google Maps script
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_API_KEY}&libraries=places&callback=initGoogleMaps`;
    script.async = true;
    script.defer = true;
    script.onerror = (error) => {
      console.error('Error loading Google Maps:', error);
      setError('Failed to load Google Maps. Please check your internet connection and try again.');
    };
    document.head.appendChild(script);

    return () => {
      // Cleanup
      if (window.initGoogleMaps) {
        delete window.initGoogleMaps;
      }
      const scriptElement = document.querySelector(`script[src*="maps.googleapis.com"]`);
      if (scriptElement) {
        document.head.removeChild(scriptElement);
      }
    };
  }, []);

  const initMap = () => {
    try {
      if (!mapRef.current || !window.google) return;

      // Default to center of US
      const defaultLocation = { lat: 39.8283, lng: -98.5795 };

      const mapInstance = new window.google.maps.Map(mapRef.current, {
        zoom: 4,
        center: defaultLocation,
        mapTypeId: 'roadmap',
        styles: [
          {
            featureType: "poi",
            elementType: "labels",
            stylers: [{ visibility: "off" }]
          }
        ]
      });

      // Add click listener
      mapInstance.addListener('click', (e) => {
        const clickedLocation = {
          lat: e.latLng.lat(),
          lng: e.latLng.lng()
        };
        updateMarkerAndCircle(clickedLocation);
        analyzeLocation(clickedLocation);
      });

      setMap(mapInstance);
    } catch (error) {
      console.error('Error initializing map:', error);
      setError('Failed to initialize map. Please refresh the page and try again.');
    }
  };

  const updateMarkerAndCircle = (location) => {
    try {
      if (!map || !window.google) return;

      // Update or create marker
      if (marker) {
        marker.setPosition(location);
      } else {
        const newMarker = new window.google.maps.Marker({
          position: location,
          map: map,
          draggable: true
        });

        newMarker.addListener('dragend', () => {
          const newLocation = {
            lat: newMarker.getPosition().lat(),
            lng: newMarker.getPosition().lng()
          };
          updateMarkerAndCircle(newLocation);
          analyzeLocation(newLocation);
        });

        setMarker(newMarker);
      }

      // Update or create search radius circle
      if (searchCircle) {
        searchCircle.setCenter(location);
      } else {
        const newCircle = new window.google.maps.Circle({
          map: map,
          center: location,
          radius: SEARCH_RADIUS,
          fillColor: '#4299e1',
          fillOpacity: 0.15,
          strokeColor: '#4299e1',
          strokeWeight: 2
        });
        setSearchCircle(newCircle);
      }

      // Pan map to location
      map.panTo(location);
      map.setZoom(14);
    } catch (error) {
      console.error('Error updating marker and circle:', error);
      setError('Failed to update map markers. Please try again.');
    }
  };

  const analyzeLocation = async (location) => {
    if (!map || !window.google) {
      setError('Google Maps is not loaded. Please refresh the page.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const service = new window.google.maps.places.PlacesService(map);
      const geocoder = new window.google.maps.Geocoder();

      // Get nearby places with error handling
      const nearbyPlaces = await new Promise((resolve, reject) => {
        service.nearbySearch({
          location: location,
          radius: SEARCH_RADIUS,
          type: ['establishment']
        }, (results, status) => {
          if (status === 'OK') {
            resolve(results);
          } else if (status === 'ZERO_RESULTS') {
            resolve([]);
          } else {
            reject(new Error(`Places API error: ${status}`));
          }
        });
      });

      // Process POI categories
      const poiCategories = {
        shopping: 0,
        restaurant: 0,
        school: 0,
        park: 0,
        transport: 0
      };

      nearbyPlaces.forEach(place => {
        const types = place.types;

        if (types.some(type => [
          'shopping_mall', 'store', 'supermarket', 'grocery_or_supermarket',
          'convenience_store', 'department_store', 'retail'
        ].includes(type))) {
          poiCategories.shopping++;
        }

        if (types.some(type => [
          'restaurant', 'food', 'cafe', 'meal_takeaway', 'meal_delivery',
          'bakery', 'bar', 'night_club'
        ].includes(type))) {
          poiCategories.restaurant++;
        }

        if (types.some(type => [
          'school', 'primary_school', 'secondary_school', 'university',
          'elementary_school', 'middle_school', 'high_school'
        ].includes(type))) {
          poiCategories.school++;
        }

        if (types.some(type => [
          'park', 'playground', 'amusement_park', 'campground',
          'rv_park', 'natural_feature'
        ].includes(type))) {
          poiCategories.park++;
        }

        if (types.some(type => [
          'transit_station', 'bus_station', 'train_station', 'subway_station',
          'airport', 'light_rail_station', 'taxi_stand'
        ].includes(type))) {
          poiCategories.transport++;
        }
      });

      // Calculate POI score
      const poiScore = Math.min(200, 
        (poiCategories.shopping * 8) +
        (poiCategories.restaurant * 10) +
        (poiCategories.school * 20) +
        (poiCategories.park * 12) +
        (poiCategories.transport * 10)
      );

      // Get place details using reverse geocoding
      const placeDetails = await new Promise((resolve, reject) => {
        geocoder.geocode({ location }, (results, status) => {
          if (status === 'OK') resolve(results[0]);
          else reject(status);
        });
      });

      // Mock sentiment data (in real app, would come from additional APIs)
      const mockRating = 4.2;
      const mockTotalRatings = 85;

      const sentimentScore = Math.min(200, Math.round((mockRating / 5) * 150 + 
        Math.min(50, mockTotalRatings / 10)));

      // Set the results
      setResults({
        poi: {
          score: poiScore,
          details: poiCategories
        },
        sentiment: {
          score: sentimentScore,
          details: {
            averageRating: mockRating.toFixed(1),
            totalReviews: mockTotalRatings,
            communityEngagement: mockTotalRatings > 100 ? 'High' : mockTotalRatings > 50 ? 'Medium' : 'Low'
          }
        },
        propertyDetails: {
          address: placeDetails.formatted_address,
          coordinates: location,
          nearbyPOIs: nearbyPlaces.length
        }
      });

      // Log found places for debugging
      console.log('Found Places:', nearbyPlaces.map(place => ({
        name: place.name,
        types: place.types
      })));

    } catch (error) {
      console.error('Analysis error:', error);
      setError('Error analyzing location. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearchSubmit = async () => {
    if (!searchInput.trim()) {
      setError('Please enter an address or coordinates');
      return;
    }

    try {
      let location;
      if (isCoordinates(searchInput)) {
        const [lat, lng] = searchInput.split(',').map(coord => parseFloat(coord.trim()));
        location = { lat, lng };
      } else {
        const geocoder = new window.google.maps.Geocoder();
        const result = await new Promise((resolve, reject) => {
          geocoder.geocode({ address: searchInput }, (results, status) => {
            if (status === 'OK') resolve(results[0]);
            else reject(status);
          });
        });
        location = {
          lat: result.geometry.location.lat(),
          lng: result.geometry.location.lng()
        };
      }

      updateMarkerAndCircle(location);
      analyzeLocation(location);
    } catch (error) {
      setError('Could not find location. Please check the address or coordinates.');
      console.error('Search error:', error);
    }
  };

  const ScoreCard = ({ title, score, details }) => (
    <Card className="w-full mb-4">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold">{title}</h3>
        <div className="text-2xl font-bold">{score}/200</div>
      </div>
      <div className="text-sm text-gray-600">
        {Object.entries(details).map(([key, value]) => (
          <div key={key} className="flex justify-between mt-1">
            <span>{key.replace(/([A-Z])/g, ' $1').trim()}:</span>
            <span className="font-medium">{value}</span>
          </div>
        ))}
      </div>
    </Card>
  );

  const calculateTotalScore = (results) => {
    return Object.values(results)
      .filter(category => typeof category.score === 'number')
      .reduce((total, category) => total + category.score, 0);
  };

  if (!googleLoaded) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <div className="text-xl mb-4">Loading Google Maps...</div>
          {error && <div className="text-red-500">{error}</div>}
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Left column: Map */}
        <div className="space-y-4">
          <Card>
            <h2 className="text-2xl font-bold mb-4">Select Location</h2>
            <div className="flex flex-col gap-4">
              <div className="flex gap-4">
                <Input
                  type="text"
                  placeholder="Enter address or coordinates (lat,lng)"
                  value={searchInput}
                  onChange={(e) => setSearchInput(e.target.value)}
                />
                <Button
                  onClick={handleSearchSubmit}
                  disabled={loading}
                >
                  {loading ? 'Analyzing...' : 'Search'}
                </Button>
              </div>
              {error && (
                <div className="text-red-500 text-sm">{error}</div>
              )}
            </div>
          </Card>

          <div 
            ref={mapRef} 
            className="w-full h-[600px] rounded-lg shadow-lg border border-gray-200"
          ></div>

          <div className="text-sm text-gray-600 italic">
            Click anywhere on the map to analyze that location, or drag the marker to adjust position
          </div>
        </div>

        {/* Right column: Results */}
        <div className="space-y-4">
          {results && (
            <>
              <ScoreCard
                title="Points of Interest"
                score={results.poi.score}
                details={results.poi.details}
              />
              <ScoreCard
                title="Sentiment Analysis"
                score={results.sentiment.score}
                details={results.sentiment.details}
              />

              <Card>
                <h3 className="text-xl font-bold mb-4">Overall Assessment</h3>
                <div className="text-4xl font-bold mb-4">
                  Total Score: {calculateTotalScore(results)}/1000
                </div>

                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="font-semibold mb-2">Property Details:</h4>
                  <div className="grid grid-cols-1 gap-2">
                    <div>Address: {results.propertyDetails.address}</div>
                    <div>Nearby POIs: {results.propertyDetails.nearbyPOIs}</div>
                    <div>
                      Coordinates: {results.propertyDetails.coordinates.lat.toFixed(6)}, 
                      {results.propertyDetails.coordinates.lng.toFixed(6)}
                    </div>
                  </div>
                </div>
              </Card>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default PropertyAssessmentTool;
