import React, { useState, useRef } from 'react';
import { Camera } from 'lucide-react';

interface CameraDetectionProps {
  onIngredientsDetected: (ingredients: string[]) => void;
}

const CameraDetection: React.FC<CameraDetectionProps> = ({ onIngredientsDetected }) => {
  const [isCapturing, setIsCapturing] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [permissionDenied, setPermissionDenied] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const knownIngredients = new Set([
    'apple', 'banana', 'orange', 'tomato', 'onion', 'garlic',
    'potato', 'carrot', 'broccoli', 'pepper', 'cucumber',
    'lettuce', 'spinach', 'mushroom', 'lemon', 'lime',
    'cheese', 'bread', 'egg', 'chicken', 'beef', 'fish',
    'rice', 'pasta', 'beans', 'nuts', 'herbs', 'spices'
  ]);

  const checkPermissions = async () => {
    try {
      // First, check if getUserMedia is supported
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Camera API is not supported in your browser');
      }

      // Check if we already have permissions
      const result = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'environment' }
      });

      // If we get here, permission was granted
      result.getTracks().forEach(track => track.stop()); // Clean up
      return true;
    } catch (err) {
      console.error('Permission check failed:', err);
      return false;
    }
  };

  const startCamera = async () => {
    setPermissionDenied(false);

    try {
      const hasPermission = await checkPermissions();
      if (!hasPermission) {
        setPermissionDenied(true);
        return;
      }

      const constraints = {
        video: {
          facingMode: { ideal: 'environment' },
          width: { ideal: 1920 },
          height: { ideal: 1080 }
        }
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        // For iOS Safari, we need to call play() on user interaction
        videoRef.current.play().catch(err => {
          console.error('Playback failed:', err);
        });
      }
      setIsCapturing(true);
    } catch (err) {
      console.error('Error accessing camera:', err);
      setPermissionDenied(true);
      alert('Unable to access camera. Please ensure you have granted camera permissions in your browser settings.');
    }
  };

  const stopCamera = () => {
    if (videoRef.current?.srcObject) {
      const tracks = (videoRef.current.srcObject as MediaStream).getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    setIsCapturing(false);
  };

  const captureAndDetect = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    setProcessing(true);
    try {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const ctx = canvas.getContext('2d');
      if (!ctx) throw new Error('Could not get canvas context');

      // Capture frame
      ctx.drawImage(video, 0, 0);

      // For testing purposes, simulate detection
      // Remove this and uncomment the Google Vision code for production
      setTimeout(() => {
        const mockIngredients = ['tomato', 'onion', 'garlic'];
        onIngredientsDetected(mockIngredients);
        setProcessing(false);
        stopCamera();
      }, 2000);

      /* Uncomment for actual Google Vision API usage
      const blob = await new Promise<Blob>((resolve) => {
        canvas.toBlob((b) => resolve(b!), 'image/jpeg', 0.8);
      });

      const detectedIngredients = await analyzeImageWithGoogleVision(blob);
      onIngredientsDetected(detectedIngredients);
      */

    } catch (err) {
      console.error('Error during capture:', err);
      alert('Error capturing image. Please try again.');
    } finally {
      setProcessing(false);
      stopCamera();
    }
  };

  const handlePermissionError = () => {
    if (permissionDenied) {
      return (
        <div className="text-red-500 text-center mt-2">
          Camera access was denied. Please enable camera access in your browser settings and reload the page.
        </div>
      );
    }
    return null;
  };

  return (
    <div className="w-full">
      <canvas ref={canvasRef} style={{ display: 'none' }} />

      {isCapturing ? (
        <div className="relative">
          <div className="aspect-video bg-gray-800 rounded-lg overflow-hidden">
            <video 
              ref={videoRef}
              autoPlay 
              playsInline
              muted // Required for iOS
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="w-24 h-24 rounded-full border-4 border-white opacity-50"></div>
            </div>
          </div>
          <div className="absolute bottom-4 left-0 right-0 flex justify-center space-x-4">
            <button
              onClick={stopCamera}
              className="p-3 rounded-full bg-red-500 text-white hover:bg-red-600"
            >
              Cancel
            </button>
            <button
              onClick={captureAndDetect}
              disabled={processing}
              className="p-3 rounded-full text-white hover:opacity-90"
              style={{
                backgroundColor: processing ? '#B7B7A4' : '#6B705C'
              }}
            >
              {processing ? 'Detecting...' : 'Capture'}
            </button>
          </div>
        </div>
      ) : (
        <>
          <button
            onClick={startCamera}
            className="flex items-center justify-center w-full p-3 rounded-lg space-x-2 transition-all hover:scale-105"
            style={{ backgroundColor: '#B7B7A4' }}
          >
            <Camera className="w-6 h-6" />
            <span>Scan Ingredients</span>
          </button>
          {handlePermissionError()}
        </>
      )}

      {processing && (
        <div className="mt-4 flex items-center justify-center space-x-2 p-4 rounded-lg" 
             style={{ backgroundColor: '#FFE8D6' }}>
          <div className="w-4 h-4 rounded-full bg-gray-600 animate-pulse"></div>
          <div className="w-4 h-4 rounded-full bg-gray-600 animate-pulse delay-100"></div>
          <div className="w-4 h-4 rounded-full bg-gray-600 animate-pulse delay-200"></div>
          <span className="ml-2 text-gray-800">Processing image...</span>
        </div>
      )}
    </div>
  );
};

export default CameraDetection;
