import React, { useState, useEffect, useRef } from 'react';

const PropertyAssessmentTool = () => {
  const [searchInput, setSearchInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState('');
  const [googleLoaded, setGoogleLoaded] = useState(false);
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);
  const [searchCircle, setSearchCircle] = useState(null);
  const [poiMarkers, setPoiMarkers] = useState([]);
  const [showPOIMarkers, setShowPOIMarkers] = useState(true);
  const [mapType, setMapType] = useState('roadmap');
  const [searchRadius, setSearchRadius] = useState(3000);
  const mapRef = useRef(null);
  const markersRef = useRef([]);

  const GOOGLE_API_KEY = 'AIzaSyBf5WWjQksCtxIziKvs7kFq4qjPCf6SoZQ';

  // Continue with Part 2...
  // ... continuing from Part 1

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_API_KEY}&libraries=places`;
    script.async = true;
    script.onload = () => {
      setGoogleLoaded(true);
      initMap();
    };
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  useEffect(() => {
    if (map) {
      map.setMapTypeId(mapType);
    }
  }, [mapType]);

  useEffect(() => {
    if (searchCircle) {
      searchCircle.setRadius(searchRadius);
    }
  }, [searchRadius]);

  useEffect(() => {
    poiMarkers.forEach(marker => {
      marker.setVisible(showPOIMarkers);
    });
  }, [showPOIMarkers]);

  const clearPOIMarkers = () => {
    poiMarkers.forEach(marker => marker.setMap(null));
    setPoiMarkers([]);
  };

  const createPOIMarker = (place, category) => {
    const marker = new window.google.maps.Marker({
      position: place.geometry.location,
      map: showPOIMarkers ? map : null,
      title: place.name,
      icon: {
        url: `https://maps.google.com/mapfiles/ms/icons/${getPOIColor(category)}-dot.png`,
        scaledSize: new window.google.maps.Size(32, 32)
      }
    });

    const infoWindow = new window.google.maps.InfoWindow({
      content: `
        <div class="p-2">
          <strong>${place.name}</strong><br/>
          Type: ${category}<br/>
          ${place.vicinity || ''}
        </div>
      `
    });

    marker.addListener('click', () => {
      infoWindow.open(map, marker);
    });

    return marker;
  };

  const getPOIColor = (category) => {
    const colors = {
      shopping: 'yellow',
      restaurant: 'red',
      school: 'blue',
      park: 'green',
      transport: 'purple'
    };
    return colors[category] || 'red';
  };

  const initMap = () => {
    const defaultLocation = { lat: 39.8283, lng: -98.5795 };

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      zoom: 4,
      center: defaultLocation,
      mapTypeId: mapType,
      mapTypeControl: true,
      mapTypeControlOptions: {
        style: window.google.maps.MapTypeControlStyle.DROPDOWN_MENU
      },
      zoomControl: true,
      zoomControlOptions: {
        position: window.google.maps.ControlPosition.RIGHT_TOP
      },
      scaleControl: true,
      streetViewControl: true,
      fullscreenControl: true
    });

    mapInstance.addListener('click', (e) => {
      const clickedLocation = {
        lat: e.latLng.lat(),
        lng: e.latLng.lng()
      };
      updateMarkerAndCircle(clickedLocation);
      analyzeLocation(clickedLocation);
    });

    setMap(mapInstance);
  };

  const resetView = () => {
    if (marker) {
      const position = marker.getPosition();
      map.setCenter(position);
      map.setZoom(14);
    }
  };

  // ... (keep the existing updateMarkerAndCircle, isCoordinates functions)


  const analyzeLocation = async (location) => {
    setLoading(true);
    setError('');
    clearPOIMarkers();

    try {
      const service = new window.google.maps.places.PlacesService(map);
      const geocoder = new window.google.maps.Geocoder();

      const nearbyPlaces = await new Promise((resolve, reject) => {
        service.nearbySearch({
          location: location,
          radius: searchRadius,
          type: ['establishment']
        }, (results, status) => {
          if (status === 'OK') resolve(results);
          else reject(status);
        });
      });

      const poiCategories = {
        shopping: 0,
        restaurant: 0,
        school: 0,
        park: 0,
        transport: 0
      };

      const newMarkers = [];

      nearbyPlaces.forEach(place => {
        const types = place.types;
        let category = null;

        if (types.some(type => ['shopping_mall', 'store', 'supermarket', 'grocery_or_supermarket',
          'convenience_store', 'department_store', 'retail'].includes(type))) {
          poiCategories.shopping++;
          category = 'shopping';
        }

        if (types.some(type => ['restaurant', 'food', 'cafe', 'meal_takeaway', 'meal_delivery',
          'bakery', 'bar', 'night_club'].includes(type))) {
          poiCategories.restaurant++;
          category = 'restaurant';
        }

        if (types.some(type => ['school', 'primary_school', 'secondary_school', 'university',
          'elementary_school', 'middle_school', 'high_school'].includes(type))) {
          poiCategories.school++;
          category = 'school';
        }

        if (types.some(type => ['park', 'playground', 'amusement_park', 'campground',
          'rv_park', 'natural_feature'].includes(type))) {
          poiCategories.park++;
          category = 'park';
        }

        if (types.some(type => ['transit_station', 'bus_station', 'train_station', 'subway_station',
          'airport', 'light_rail_station', 'taxi_stand'].includes(type))) {
          poiCategories.transport++;
          category = 'transport';
        }

        if (category) {
          newMarkers.push(createPOIMarker(place, category));
        }
      });

      setPoiMarkers(newMarkers);

      // ... (keep the existing score calculation logic)

      setResults({
        poi: {
          score: poiScore,
          details: poiCategories
        },
        sentiment: {
          score: sentimentScore,
          details: {
            averageRating: mockRating.toFixed(1),
            totalReviews: mockTotalRatings,
            communityEngagement: mockTotalRatings > 100 ? 'High' : mockTotalRatings > 50 ? 'Medium' : 'Low'
          }
        },
        propertyDetails: {
          address: placeDetails.formatted_address,
          coordinates: location,
          nearbyPOIs: nearbyPlaces.length,
          searchRadius: searchRadius
        }
      });

    } catch (error) {
      setError('Error analyzing location. Please try again.');
      console.error('Analysis error:', error);
    } finally {
      setLoading(false);
    }
  };
// Custom components with additional styling
const Card = ({ children, className = '' }) => (
  <div className={`bg-white rounded-lg shadow p-4 ${className}`}>
    {children}
  </div>
);

const isCoordinates = (input) => {
  // Basic coordinate validation (lat,lng)
  const coordPattern = /^[-+]?([1-8]?\d(\.\d+)?|90(\.0+)?),\s*[-+]?(180(\.0+)?|((1[0-7]\d)|([1-9]?\d))(\.\d+)?)$/;
  return coordPattern.test(input);
};
const Input = ({ ...props }) => (
  <input
    {...props}
    className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
  />
);

const Button = ({ onClick, children, disabled, className = '' }) => (
  <button
    onClick={onClick}
    className={`px-4 py-2 rounded-lg text-white font-medium ${
      disabled 
        ? 'bg-gray-400 cursor-not-allowed' 
        : 'bg-blue-500 hover:bg-blue-600 active:bg-blue-700'
    } ${className}`}
    disabled={disabled}
  >
    {children}
  </button>
);

const updateMarkerAndCircle = (location) => {
  // Update or create marker
  if (marker) {
    marker.setPosition(location);
  } else {
    const newMarker = new window.google.maps.Marker({
      position: location,
      map: map,
      draggable: true
    });

    newMarker.addListener('dragend', () => {
      const newLocation = {
        lat: newMarker.getPosition().lat(),
        lng: newMarker.getPosition().lng()
      };
      updateMarkerAndCircle(newLocation);
      analyzeLocation(newLocation);
    });

    setMarker(newMarker);
  }

  // Update or create search radius circle
  if (searchCircle) {
    searchCircle.setCenter(location);
  } else {
    const newCircle = new window.google.maps.Circle({
      map: map,
      center: location,
      radius: searchRadius,
      fillColor: '#4299e1',
      fillOpacity: 0.15,
      strokeColor: '#4299e1',
      strokeWeight: 2
    });
    setSearchCircle(newCircle);
  }

  // Pan map to location
  map.panTo(location);
  map.setZoom(14);
};

const handleSearchSubmit = async () => {
  if (!searchInput.trim()) {
    setError('Please enter an address or coordinates');
    return;
  }

  try {
    let location;
    if (isCoordinates(searchInput)) {
      const [lat, lng] = searchInput.split(',').map(coord => parseFloat(coord.trim()));
      location = { lat, lng };
    } else {
      const geocoder = new window.google.maps.Geocoder();
      const result = await new Promise((resolve, reject) => {
        geocoder.geocode({ address: searchInput }, (results, status) => {
          if (status === 'OK') resolve(results[0]);
          else reject(status);
        });
      });
      location = {
        lat: result.geometry.location.lat(),
        lng: result.geometry.location.lng()
      };
    }

    updateMarkerAndCircle(location);
    analyzeLocation(location);
  } catch (error) {
    setError('Could not find location. Please check the address or coordinates.');
    console.error('Search error:', error);
  }
};

