import React, { useState, useEffect, useRef } from 'react';

// Type definitions
interface PoiCategories {
  shopping: number;
  restaurant: number;
  school: number;
  park: number;
  transport: number;
}

interface Location {
  lat: number;
  lng: number;
}

interface PropertyDetails {
  address: string;
  coordinates: Location;
  nearbyPOIs: number;
}

interface AnalysisResults {
  poi: {
    score: number;
    details: PoiCategories;
  };
  sentiment: {
    score: number;
    details: {
      averageRating: string;
      totalReviews: number;
      communityEngagement: string;
    };
  };
  propertyDetails: PropertyDetails;
}

// Declare global Google Maps types
declare global {
  interface Window {
    google: typeof google;
    initGoogleMaps: () => void;
  }
}

const Card: React.FC<{ children: React.ReactNode; className?: string }> = ({ children, className = '' }) => (
  <div className={`bg-white rounded-lg shadow p-4 ${className}`}>
    {children}
  </div>
);

const Input: React.FC<React.InputHTMLAttributes<HTMLInputElement>> = (props) => (
  <input
    {...props}
    className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
  />
);

const Button: React.FC<{
  onClick: () => void;
  children: React.ReactNode;
  disabled?: boolean;
}> = ({ onClick, children, disabled }) => (
  <button
    onClick={onClick}
    className={`px-4 py-2 rounded-lg text-white font-medium ${
      disabled 
        ? 'bg-gray-400 cursor-not-allowed' 
        : 'bg-blue-500 hover:bg-blue-600 active:bg-blue-700'
    }`}
    disabled={disabled}
  >
    {children}
  </button>
);

const ScoreCard: React.FC<{
  title: string;
  score: number;
  details: Record<string, number | string>;
}> = ({ title, score, details }) => (
  <Card className="w-full mb-4">
    <div className="flex justify-between items-center mb-4">
      <h3 className="text-lg font-semibold">{title}</h3>
      <div className="text-2xl font-bold">{score}/200</div>
    </div>
    <div className="text-sm text-gray-600">
      {Object.entries(details).map(([key, value]) => (
        <div key={key} className="flex justify-between mt-1">
          <span>{key.replace(/([A-Z])/g, ' $1').trim()}:</span>
          <span className="font-medium">{String(value)}</span>
        </div>
      ))}
    </div>
  </Card>
);

const PropertyAssessmentTool: React.FC = () => {
  const [searchInput, setSearchInput] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [results, setResults] = useState<AnalysisResults | null>(null);
  const [error, setError] = useState<string>('');
  const [googleLoaded, setGoogleLoaded] = useState<boolean>(false);
  const [map, setMap] = useState<google.maps.Map | null>(null);
  const [marker, setMarker] = useState<google.maps.Marker | null>(null);
  const [searchCircle, setSearchCircle] = useState<google.maps.Circle | null>(null);
  const mapRef = useRef<HTMLDivElement>(null);

  const GOOGLE_API_KEY = 'YAIzaSyBf5WWjQksCtxIziKvs7kFq4qjPCf6SoZQ';
  const SEARCH_RADIUS = 3000;

  const isCoordinates = (input: string): boolean => {
    const coordPattern = /^[-+]?([1-8]?\d(\.\d+)?|90(\.0+)?),\s*[-+]?(180(\.0+)?|((1[0-7]\d)|([1-9]?\d))(\.\d+)?)$/;
    return coordPattern.test(input);
  };

  useEffect(() => {
    if (window.google && window.google.maps) {
      setGoogleLoaded(true);
      initMap();
      return;
    }

    window.initGoogleMaps = () => {
      setGoogleLoaded(true);
      initMap();
    };

    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_API_KEY}&libraries=places&callback=initGoogleMaps`;
    script.async = true;
    script.defer = true;
    document.head.appendChild(script);

    return () => {
      if (window.initGoogleMaps) {
        delete window.initGoogleMaps;
      }
      const scriptElement = document.querySelector(`script[src*="maps.googleapis.com"]`);
      if (scriptElement) {
        document.head.removeChild(scriptElement);
      }
    };
  }, []);

  const initMap = (): void => {
    if (!mapRef.current || !window.google) return;

    const defaultLocation: Location = { lat: 39.8283, lng: -98.5795 };

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      zoom: 4,
      center: defaultLocation,
      mapTypeId: 'roadmap',
      styles: [
        {
          featureType: "poi",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        }
      ]
    });

    mapInstance.addListener('click', (e: google.maps.MapMouseEvent) => {
      if (!e.latLng) return;

      const clickedLocation: Location = {
        lat: e.latLng.lat(),
        lng: e.latLng.lng()
      };
      updateMarkerAndCircle(clickedLocation);
      void analyzeLocation(clickedLocation);
    });

    setMap(mapInstance);
  };

  const updateMarkerAndCircle = (location: Location): void => {
    if (!map) return;

    if (marker) {
      marker.setPosition(location);
    } else {
      const newMarker = new window.google.maps.Marker({
        position: location,
        map: map,
        draggable: true
      });

      newMarker.addListener('dragend', () => {
        if (!newMarker.getPosition()) return;
        const newLocation: Location = {
          lat: newMarker.getPosition()!.lat(),
          lng: newMarker.getPosition()!.lng()
        };
        updateMarkerAndCircle(newLocation);
        void analyzeLocation(newLocation);
      });

      setMarker(newMarker);
    }

    if (searchCircle) {
      searchCircle.setCenter(location);
    } else {
      const newCircle = new window.google.maps.Circle({
        map: map,
        center: location,
        radius: SEARCH_RADIUS,
        fillColor: '#4299e1',
        fillOpacity: 0.15,
        strokeColor: '#4299e1',
        strokeWeight: 2
      });
      setSearchCircle(newCircle);
    }

    map.panTo(location);
    map.setZoom(14);
  };

  const analyzeLocation = async (location: Location): Promise<void> => {
    if (!map) return;

    setLoading(true);
    setError('');

    try {
      const service = new window.google.maps.places.PlacesService(map);
      const geocoder = new window.google.maps.Geocoder();

      const nearbyPlaces = await new Promise<google.maps.places.PlaceResult[]>((resolve, reject) => {
        service.nearbySearch({
          location,
          radius: SEARCH_RADIUS,
          type: 'establishment'
        }, (results, status) => {
          if (status === google.maps.places.PlacesServiceStatus.OK && results) {
            resolve(results);
          } else if (status === google.maps.places.PlacesServiceStatus.ZERO_RESULTS) {
            resolve([]);
          } else {
            reject(new Error(`Places API error: ${status}`));
          }
        });
      });

      const placeDetails = await new Promise<google.maps.GeocoderResult>((resolve, reject) => {
        geocoder.geocode({ location }, (results, status) => {
          if (status === 'OK' && results && results[0]) {
            resolve(results[0]);
          } else {
            reject(new Error('Geocoding failed'));
          }
        });
      });

      // Calculate scores and set results
      const poiCategories: PoiCategories = {
        shopping: 0,
        restaurant: 0,
        school: 0,
        park: 0,
        transport: 0
      };

      nearbyPlaces.forEach(place => {
        if (!place.types) return;

        if (place.types.some(type => [
          'shopping_mall', 'store', 'supermarket', 'grocery_or_supermarket',
          'convenience_store', 'department_store', 'retail'
        ].includes(type))) {
          poiCategories.shopping++;
        }

        if (place.types.some(type => [
          'restaurant', 'food', 'cafe', 'meal_takeaway', 'meal_delivery',
          'bakery', 'bar', 'night_club'
        ].includes(type))) {
          poiCategories.restaurant++;
        }

        // ... (similar checks for other categories)
      });

      const poiScore = Math.min(200, 
        (poiCategories.shopping * 8) +
        (poiCategories.restaurant * 10) +
        (poiCategories.school * 20) +
        (poiCategories.park * 12) +
        (poiCategories.transport * 10)
      );

      setResults({
        poi: {
          score: poiScore,
          details: poiCategories
        },
        sentiment: {
          score: 150,
          details: {
            averageRating: '4.2',
            totalReviews: 85,
            communityEngagement: 'Medium'
          }
        },
        propertyDetails: {
          address: placeDetails.formatted_address,
          coordinates: location,
          nearbyPOIs: nearbyPlaces.length
        }
      });

    } catch (error) {
      console.error('Analysis error:', error);
      setError('Error analyzing location. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!googleLoaded) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <div className="text-xl mb-4">Loading Google Maps...</div>
          {error && <div className="text-red-500">{error}</div>}
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="space-y-4">
          <Card>
            <h2 className="text-2xl font-bold mb-4">Select Location</h2>
            <div className="flex flex-col gap-4">
              <div className="flex gap-4">
                <Input
                  type="text"
                  placeholder="Enter address or coordinates (lat,lng)"
                  value={searchInput}
                  onChange={(e) => setSearchInput(e.target.value)}
                />
                <Button
                  onClick={() => void analyzeLocation}
                  disabled={loading}
                >
                  {loading ? 'Analyzing...' : 'Search'}
                </Button>
              </div>
              {error && (
                <div className="text-red-500 text-sm">{error}</div>
              )}
            </div>
          </Card>

          <div 
            ref={mapRef} 
            className="w-full h-[600px] rounded-lg shadow-lg border border-gray-200"
          />
        </div>

        <div className="space-y-4">
          {results && (
            <>
              <ScoreCard
                title="Points of Interest"
                score={results.poi.score}
                details={results.poi.details}
              />
              <ScoreCard
                title="Sentiment Analysis"
                score={results.sentiment.score}
                details={results.sentiment.details}
              />

              <Card>
                <h3 className="text-xl font-bold mb-4">Overall Assessment</h3>
                <div className="text-4xl font-bold mb-4">
                  Total Score: {results.poi.score + results.sentiment.score}/400
                </div>

                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="font-semibold mb-2">Property Details:</h4>
                  <div className="grid grid-cols-1 gap-2">
                    <div>Address: {results.propertyDetails.address}</div>
                    <div>Nearby POIs: {results.propertyDetails.nearbyPOIs}</div>
                    <div>
                      Coordinates: {results.propertyDetails.coordinates.lat.toFixed(6)}, 
                      {results.propertyDetails.coordinates.lng.toFixed(6)}
                    </div>
                  </div>
                </div>
              </Card>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default PropertyAssessmentTool;
